import React, { useState, useEffect } from 'react';
import '../static/operations.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSearch, faChevronLeft, faChevronRight } from '@fortawesome/free-solid-svg-icons';
import useOperationsAPI from '../api/operationsAPI';
import CustomAlert from '../components/customAlert';

const changeDate = (delta: number): void => {
  const dateSelector = document.getElementById('dateSelector') as HTMLInputElement | null;

  if (dateSelector) {
    const currentDate = new Date(dateSelector.value);
    currentDate.setDate(currentDate.getDate() + delta);

    const year = currentDate.getFullYear();
    let month = `${currentDate.getMonth() + 1}`;
    month = month.length === 1 ? `0${month}` : month;
    let day = `${currentDate.getDate()}`;
    day = day.length === 1 ? `0${day}` : day;

    const newDate = `${year}-${month}-${day}`;
    dateSelector.value = newDate;
  }
};

const FlashingCell: React.FC<{ value: any }> = ({ value }) => {
  const [previousValue, setPreviousValue] = useState<any | null>(null);

  useEffect(() => {
    if (previousValue !== null && previousValue !== value) {
      const cellElement = document.getElementById(`cell-${value}`);
      if (cellElement) {
        cellElement.style.backgroundColor = 'yellow';
        setTimeout(() => {
          cellElement.style.backgroundColor = 'white';
        }, 1500);
      }
    }

    setPreviousValue(value);
  }, [value, previousValue]);

  return <span id={`cell-${value}`}>{value}</span>;
};



const Operations: React.FC = () => {

  const { apiData, fetchData } = useOperationsAPI();
  const [showAlert, setShowAlert] = useState(false);

  const DataChangeHandler: React.FC<{ apiData: any[] }> = ({ apiData }) => {
    const [previousData, setPreviousData] = useState<{ low_24h: number; high_24h: number }>({
      low_24h: 0,
      high_24h: 0,
    });

    useEffect(() => {
      if (apiData && apiData.length > 0) {
        const hasValueChanged = apiData.some((data) => {
          const { low_24h, high_24h } = data;
          return (
            low_24h !== undefined &&
            high_24h !== undefined &&
            (low_24h !== previousData.low_24h || high_24h !== previousData.high_24h)
          );
        });

        if (hasValueChanged) {
          // Mostrar la alerta
          setShowAlert(true);

          // Después de 10 segundos, ocultar la alerta
          setTimeout(() => {
            setShowAlert(false);
          }, 10000);
        }

        // Actualizar los valores anteriores
        const firstData = apiData[0];
        if (firstData) {
          setPreviousData({
            low_24h: firstData.low_24h || 0,
            high_24h: firstData.high_24h || 0,
          });
        }
      }
    }, [apiData, previousData]);

    return null;
  };

  useEffect(() => {
    const intervalId = window.setInterval(() => {
      fetchData();
    }, 120000);

    return () => clearInterval(intervalId);
  }, [fetchData]);

  return (
    <div className="operations-container">
      <DataChangeHandler apiData={apiData} />

      <div className="header-section">
        <div className="left-subsection">
          <label htmlFor="dateSelector"></label>
          <div className="date-picker-arrows">
            <button onClick={() => changeDate(-1)}><FontAwesomeIcon icon={faChevronLeft} /></button>
            <input type="date" id="dateSelector" />
            <button onClick={() => changeDate(1)}><FontAwesomeIcon icon={faChevronRight} /></button>
          </div>

          <label htmlFor="searchInput">
            <FontAwesomeIcon icon={faSearch} />
          </label>
          <input type="text" id="searchInput" placeholder="Flight, Orig, Dest, Reg" />
        </div>

        <div className="right-subsection">
          <h1>Operations</h1>
        </div>
      </div>

      <div className="table-section">
        <table>
          <thead>
            <tr>
              <th>ID</th>
              <th>Name</th>
              <th>Current Price</th>
              <th>Last Updated</th>
              <th>Highest 24h</th>
              <th>Lowest 24h</th>
            </tr>
          </thead>
          <tbody>
            {apiData?.map((data, index) => (
              <tr key={index}>
                <td><FlashingCell value={data.id} /></td>
                <td><FlashingCell value={data.name} /></td>
                <td><FlashingCell value={data.current_price} /></td>
                <td><FlashingCell value={data.last_updated} /></td>
                <td><FlashingCell value={data.high_24h} /></td>
                <td><FlashingCell value={data.low_24h} /></td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Mostrar la alerta si showAlert es true */}
      {showAlert && <CustomAlert message="¡Esta es una alerta personalizada!" onClose={() => setShowAlert(false)} />}
    </div>
  );
};

export default Operations;
