import React, { useState, useEffect } from 'react';
import '../static/operations.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSearch, faChevronLeft, faChevronRight } from '@fortawesome/free-solid-svg-icons';
import OperationsAPI from '../api/operationsAPI';
import CustomAlert from '../components/customAlert';
import FlightsAPI from '../api/flightsAPI';

const changeDate = (delta: number): void => {
    const dateSelector = document.getElementById('dateSelector') as HTMLInputElement | null;

    if (dateSelector) {
        const currentDate = new Date(dateSelector.value);
        currentDate.setDate(currentDate.getDate() + delta);

        const year = currentDate.getFullYear();
        let month = `${currentDate.getMonth() + 1}`;
        month = month.length === 1 ? `0${month}` : month;
        let day = `${currentDate.getDate()}`;
        day = day.length === 1 ? `0${day}` : day;

        const newDate = `${year}-${month}-${day}`;
        dateSelector.value = newDate;
    }
};

const formatUnixTimestamp = (timestamp: number): string => {
    const date = new Date(timestamp * 1000);
    const day = date.getDate().toString().padStart(2, '0');
    const month = (date.getMonth() + 1).toString().padStart(2, '0');
    const year = date.getFullYear();
    const hours = date.getHours().toString().padStart(2, '0');
    const minutes = date.getMinutes().toString().padStart(2, '0');
    return `${day}-${month}-${year} ${hours}:${minutes}`;
  };

const FlashingCell: React.FC<{ value: any; onAttributeChange?: () => void }> = ({ value, onAttributeChange }) => {
    const [previousValue, setPreviousValue] = useState<any | null>(null);

    useEffect(() => {
        if ( previousValue !== null && previousValue !== value) {
            if (onAttributeChange){
                onAttributeChange();
            }
            const cellElement = document.getElementById(`cell-${value}`);
            if (cellElement) {
                cellElement.style.backgroundColor = 'yellow';
                setTimeout(() => {
                    cellElement.style.backgroundColor = 'white';
                }, 5000);
            }
        }

        setPreviousValue(value);
    }, [value, previousValue, onAttributeChange]);

    let displayValue;

    if (value === null) {
        displayValue = 'not available';
    } else {
        displayValue = value;
    }

    return <span id={`cell-${value}`}>{displayValue}</span>;
};



const Operations: React.FC = () => {

    const { apiData, fetchData } = FlightsAPI();
    const [showAlert, setShowAlert] = useState(false);
    const [attributeChanges, setAttributeChanges] = useState(0);
    const [affectedNames, setAffectedNames] = useState<string[]>([]);
    const [currentPage, setCurrentPage] = useState(1);
    const pageSize = 15;

    const handleAttributeChange = (name: string, fieldName: string) => {
        if (fieldName === 'estArrivalAirport' || fieldName === 'arrivalAirportCandidatesCount') {
            setAttributeChanges((prevChanges) => prevChanges + 1);


            if (!affectedNames.includes(name)) {
                setAffectedNames((prevNames) => [...prevNames, name]);
            }
        }
    };

    useEffect(() => {
        console.log('attributeChanges', attributeChanges);
        if (attributeChanges >= 1) {
            setShowAlert(true);
            setTimeout(() => {
                setShowAlert(false);
                setAttributeChanges(0);
                setAffectedNames([]);
            }, 10000);
        }
    }, [attributeChanges]);

    //Actualizamos la informaciÃ³n que obtenemos de la api cada 2 minutos debido a las limitaciones internas de la propia api para versiones gratuitas (demo)
    useEffect(() => {
        const intervalId = window.setInterval(() => {
            fetchData();
        }, 30000);

        return () => clearInterval(intervalId);
    }, [fetchData]);

    const paginatedData = apiData.slice((currentPage - 1) * pageSize, currentPage * pageSize);

    return (
        <div className="operations-container">

            <div className="header-section">
                <div className="left-subsection">
                    <label htmlFor="dateSelector"></label>
                    <div className="date-picker-arrows">
                        <button onClick={() => changeDate(-1)}><FontAwesomeIcon icon={faChevronLeft} /></button>
                        <input type="date" id="dateSelector" />
                        <button onClick={() => changeDate(1)}><FontAwesomeIcon icon={faChevronRight} /></button>
                    </div>

                    <label htmlFor="searchInput">
                        <FontAwesomeIcon icon={faSearch} />
                    </label>
                    <input type="text" id="searchInput" placeholder="Flight, Orig, Dest, Reg" />
                </div>

                <div className="right-subsection">
                    <h1>Operations</h1>
                </div>
            </div>

            <div className="table-section">
                <table>
                    <thead>
                        <tr>
                            <th>Aircraft ID</th>
                            <th>First Seen</th>
                            <th>Departure Airport</th>
                            <th>Last Seen</th>
                            <th>Arrival Airport</th>
                            <th>Callsign</th>
                            <th>Departure Horiz. Distance</th>
                            <th>Departure Vert. Distance</th>
                            <th>Arrival Horiz. Distance</th>
                            <th>Arrival Vert. Distance</th>
                            <th>Departure Airport Candidates Count</th>
                            <th>Arrival Airport Candidates Count</th>
                        </tr>
                    </thead>
                    <tbody>
                        {paginatedData?.map((data, index) => (
                            <tr key={index}>
                                <td><FlashingCell value={data.icao24}  /></td>
                                <td><FlashingCell value={formatUnixTimestamp(data.firstSeen)} /></td>
                                <td><FlashingCell value={data.estDepartureAirport} /></td>
                                <td><FlashingCell value={formatUnixTimestamp(data.lastSeen)} /></td>
                                <td><FlashingCell value={data.estArrivalAirport} /></td>
                                <td><FlashingCell value={data.callsign} /></td>
                                <td><FlashingCell value={data.estDepartureAirportHorizDistance} /></td>
                                <td><FlashingCell value={data.estDepartureAirportVertDistance} /></td>
                                <td><FlashingCell value={data.estArrivalAirportHorizDistance} /></td>
                                <td><FlashingCell value={data.estArrivalAirportVertDistance} /></td>
                                <td><FlashingCell value={data.departureAirportCandidatesCount} onAttributeChange={() => handleAttributeChange(data.icao24, 'departureAirportCandidatesCount')} /></td>
                                <td><FlashingCell value={data.arrivalAirportCandidatesCount} onAttributeChange={() => handleAttributeChange(data.icao24, 'arrivalAirportCandidatesCount')} /></td>


                            </tr>
                        ))}
                    </tbody>
                </table>

                <div className="pagination">
                <button onClick={() => setCurrentPage(currentPage - 1)} disabled={currentPage === 1}>
                    Anterior
                </button>
                <span>PÃ¡gina {currentPage}</span>
                <button
                    onClick={() => setCurrentPage(currentPage + 1)}
                    disabled={currentPage * pageSize >= apiData.length}
                >
                    Siguiente
                </button>
            </div>

            </div>

            {showAlert && <CustomAlert message={`Â¡Se han modificado los valores mÃ¡ximos y/o mÃ­nimos para: ${affectedNames.join(', ')}!`} onClose={() => setShowAlert(false)} />}
        </div>
    );
};

export default Operations;
