import React, { useEffect } from 'react';
import '../static/operations.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSearch, faChevronLeft, faChevronRight, faSyncAlt } from '@fortawesome/free-solid-svg-icons';
import { useState } from 'react';
import OperationsAPI from '../api/operationsAPI';


// function changeDate(delta) {
//     const dateSelector = document.getElementById('dateSelector');
//     const currentDate = new Date(dateSelector.value);
//     currentDate.setDate(currentDate.getDate() + delta);

//     const year = currentDate.getFullYear();
//     let month = `${currentDate.getMonth() + 1}`;
//     month = month.length === 1 ? `0${month}` : month;
//     let day = `${currentDate.getDate()}`;
//     day = day.length === 1 ? `0${day}` : day;

//     const newDate = `${year}-${month}-${day}`;
//     dateSelector.value = newDate;
// }

// const apiData = [
//     { flightNumber: 'ABC123', origin: 'Airport A', destination: 'Airport B', status: 'On Time' },
//     { flightNumber: 'ABC123', origin: 'Airport A', destination: 'Airport B', status: 'On Time' },
//   ];

function Operations () {

    const [buttonClicked, setButtonClicked] = useState(false);
    const [apiData, setApiData] = useState([]);

    // const apiData = OperationsAPI();  // Llamada a OperationsAPI como una función

    const click = () => {
      setButtonClicked(true);

      // Aquí puedes realizar cualquier otra lógica que necesites en respuesta al clic del botón
    };

     useEffect(() => {
      if (buttonClicked) {
        const apiData = OperationsAPI();
      }
    }
    );



    return (
        <div className="operations-container">
            <div className="header-section">
                <div className="left-subsection">
                    <label htmlFor="dateSelector"></label>
                    <div className="date-picker-arrows">
                        {/* <button onClick={() => changeDate(-1)}><FontAwesomeIcon icon={faChevronLeft} /></button> */}
                        <input type="date" id="dateSelector" />
                        {/* <button onClick={() => changeDate(1)}><FontAwesomeIcon icon={faChevronRight} /></button> */}
                    </div>

                    <label htmlFor="searchInput">
                        <FontAwesomeIcon icon={faSearch} />
                    </label>
                    <input type="text" id="searchInput" placeholder="Flight, Orig, Dest, Reg" />
                </div>

                <div className="right-subsection">
                    <h1>Operations</h1>
                </div>
            </div>

             <div className="additional-button-section">
        <button onClick={click}><FontAwesomeIcon icon={faSyncAlt} /> Refresh Data</button>
      </div> 

            <div className="table-section">
        <table>
          <thead>
            <tr>
              <th>ID</th>
              <th>Name</th>
              <th>Current Price</th>
              <th>Last Updated</th>
              <th>Highest 24h</th>
              <th>Lowest 24h</th>
            </tr>
          </thead>
          <tbody>
          {apiData.map((data, index) => (
              <tr key={index}>
                <td>{data.id}</td>
                <td>{data.name}</td>
                <td>{data.current_price}</td>
                <td>{data.last_updated}</td>
                <td>{data.high_24h}</td>
                <td>{data.low_24h}</td>

              </tr>
            ))}
          </tbody>
        </table>
      </div>
        </div>
    );
};

export default Operations;
