import { useState, useEffect } from 'react';
import axios from 'axios';
import CustomAlert from '../components/customAlert'; // Asegúrate de importar tu componente de alerta

interface FlightData {
  icao24: string;
  firstSeen: number;
  estDepartureAirport: string;
  lastSeen: number;
  estArrivalAirport: string;
  callsign: string;
  estDepartureAirportHorizDistance: number;
  estDepartureAirportVertDistance: number;
  estArrivalAirportHorizDistance: number;
  estArrivalAirportVertDistance: number;
  departureAirportCandidatesCount: number;
  arrivalAirportCandidatesCount: number;
}

interface FlightsAPIProps {
  searchDate?: string;
  searchText?: string;
}

const FlightsAPI = ({ searchDate, searchText }: FlightsAPIProps): { apiData: FlightData[]; fetchData: () => Promise<void> } => {
  const [apiData, setApiData] = useState<FlightData[]>([]);
  const [error, setError] = useState<string | null>(null);

  const currentTimeInSeconds = Math.floor(Date.now() / 1000);
  const twoYearsAgo = currentTimeInSeconds - 2 * 365 * 24 * 60 * 60; // Hace 2 años
  const twoYearsAgoPlusOneHour = twoYearsAgo + 3600; // Hace 2 años y 1 hora

  const fetchData = async () => {
    try {
      const response = await axios.get(
        'https://opensky-network.org/api/flights/arrival',
        {
          params: {
            airport: searchText || 'EDDF',
            // le sumo 43200000 para que obtenga una hora en la que haya tráfico aéreo. usualmente a las 00:00 no hay tráfico aéreo
            begin: searchDate ? Math.floor(new Date(searchDate).getTime() / 1000) + 43200 : twoYearsAgo,
            end: searchDate ? Math.floor(new Date(searchDate).getTime() / 1000) + 3600 + 43200 : twoYearsAgoPlusOneHour,
          },
        }
      );

      setApiData(response.data);
      setError(null); // Limpiar el estado de error si la solicitud es exitosa
    } catch (error) {
      console.error('Error fetching flight data:', error);
      setError('Error fetching flight data');
    }
  };

  useEffect(() => {
    fetchData();
  }, []); // Asegúrate de incluir los parámetros como dependencias para que se ejecute cuando cambien

  return { apiData, fetchData };
};

export default FlightsAPI;
