import { useState, useEffect } from 'react';
import axios from 'axios';

interface CoinData {
  id: string;
  high_24h: number;
  low_24h: number;
  last_updated: string;
  price_change_24h: number;
  name: string;
  current_price: number;
  market_cap: number;
}

const useOperationsAPI = (): { apiData: CoinData[]; fetchData: () => void } => {
  const [apiData, setApiData] = useState<CoinData[]>([]);

  const fetchData = async () => {
    try {
      const timestamp = new Date().getTime();
      const response = await axios.get('https://api.coingecko.com/api/v3/coins/markets', {
        params: {
          vs_currency: 'usd',
          order: 'market_cap_desc',
          per_page: 100,
          page: 1,
          timestamp: timestamp,
        },
        headers: {
          Authorization: 'Bearer CG-ZqLvqfLWk3Xp3NU29yzZCWoB',
        },
      });

      const extractedData: CoinData[] = response.data.map((item: any) => ({
        id: item.id,
        high_24h: item.high_24h,
        low_24h: item.low_24h,
        last_updated: item.last_updated,
        price_change_24h: item.price_change_24h,
        name: item.name,
        current_price: item.current_price,
        market_cap: item.market_cap,
      }));

      setApiData(extractedData);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  return { apiData, fetchData };
};

export default useOperationsAPI;
