import { useState, useEffect } from 'react';
import axios from 'axios';

interface FlightData {
  icao24: string;
  firstSeen: number;
  estDepartureAirport: string;
  lastSeen: number;
  estArrivalAirport: string;
  callsign: string;
  estDepartureAirportHorizDistance: number;
  estDepartureAirportVertDistance: number;
  estArrivalAirportHorizDistance: number;
  estArrivalAirportVertDistance: number;
  departureAirportCandidatesCount: number;
  arrivalAirportCandidatesCount: number;
}

interface FlightsAPIProps {
  searchDate?: string;
  searchText?: string;
}

const FlightsAPI = ({ searchDate, searchText }: FlightsAPIProps): { apiData: FlightData[]; fetchData: () => void } => {
  const [apiData, setApiData] = useState<FlightData[]>([]);
  const currentTimeInSeconds = Math.floor(Date.now() / 1000);
  const twoYearsAgo = currentTimeInSeconds - 2 * 365 * 24 * 60 * 60; // Hace 2 años
  const twoYearsAgoPlusOneHour = twoYearsAgo + 3600; // Hace 2 años y 1 hora

  const now = new Date();
  const currentSeconds = Math.floor(now.getTime() / 1000);

  const fetchData = async () => {
    try {
      const response = await axios.get(
        'https://opensky-network.org/api/flights/arrival',
        {
          params: {
            airport: searchText || 'EDDF',
            begin: searchDate ? Math.floor(new Date(searchDate).getTime() / 1000 ) + currentSeconds : twoYearsAgo,
            end: searchDate ? Math.floor(new Date(searchDate).getTime() / 1000) + + currentSeconds + 3600 : twoYearsAgoPlusOneHour,
          },
        }
      );

      setApiData(response.data);
    } catch (error) {
      console.error('Error fetching flight data:', error);
    }
  };

  useEffect(() => {
    fetchData();
  }, []); // Asegúrate de incluir los parámetros como dependencias para que se ejecute cuando cambien

  return { apiData, fetchData };
};

export default FlightsAPI;
